# 先用暴力法
def longestDupSub(s):
    n = len(s)
    ans = ''
    j = 1
    for i in range(n):
        longest = s[i:i+j]
        temp = s[i+1:]
        while longest in temp:
            ans = longest 
            j += 1
            longest = s[i:i+j]
    return ans


s = "banana"
s= "abca"
# s = ""
print(longestDupSub(s))